package mobi.pulsus.challenge.commons.extensionsimport android.content.Contextimport android.content.Intentimport mobi.pulsus.challenge.commons.Rfun Context.share(text: String, url: String) {    val sharingIntent = Intent(Intent.ACTION_SEND)    sharingIntent.type = "text/plain"    sharingIntent.putExtra(Intent.EXTRA_TEXT, "${getString(R.string.app_name)}\n\n$text\n\n$url")    startActivity(Intent.createChooser(sharingIntent, "Share using"))}